import "core-js/modules/es.array.push.js";
import { ref, watchEffect } from 'vue';
import { LineChart } from 'vue-chart-3';
import { Chart, registerables } from 'chart.js';
import axios from 'axios';
Chart.register(...registerables);
export default {
  components: {
    LineChart
  },
  props: {
    accountId: {
      type: Number,
      required: true
    }
  },
  setup(props) {
    const chartData = ref({
      labels: [],
      datasets: [{
        label: 'Solde',
        // Updated label for total revenue
        backgroundColor: 'rgba(54, 162, 235, 0.5)',
        borderColor: 'rgb(54, 162, 235)',
        fill: false,
        data: []
      }]
    });
    const chartOptions = ref({
      responsive: true,
      maintainAspectRatio: false,
      scales: {
        y: {
          beginAtZero: false
        }
      }
    });
    async function fetchAccountDetailsAndTransactions() {
      try {
        const accountId = props.accountId;
        const accountResponse = await axios.get(`http://localhost:3000/accounts/${accountId}`);
        const accountDetails = accountResponse.data;
        const transactionsResponse = await axios.get(`http://localhost:3000/accounts/${accountId}/transactions`);
        const transactions = transactionsResponse.data;
        updateChartData(accountDetails, transactions);
      } catch (error) {
        console.error('Erreur lors de la récupération des données', error);
      }
    }
    watchEffect(() => {
      if (props.accountId) {
        fetchAccountDetailsAndTransactions();
      }
    });
    function updateChartData(accountDetails, transactions) {
      // Triez les transactions par date en ordre croissant avant de les traiter
      const sortedTransactions = transactions.sort((a, b) => new Date(a.date) - new Date(b.date));
      chartData.value.labels = [];
      chartData.value.datasets[0].data = [];
      let balance = accountDetails.balance;

      // Ajoutez la date de début et le solde initial ici
      if (sortedTransactions.length > 0) {
        // Utilisez la date juste avant la première transaction pour le solde initial
        const startDate = new Date(sortedTransactions[0].date);
        startDate.setDate(startDate.getDate() - 1);
        chartData.value.labels.push(startDate.toLocaleDateString('fr-FR'));
      } else {
        // Si aucune transaction, utilisez la date actuelle
        chartData.value.labels.push(new Date().toLocaleDateString('fr-FR'));
      }
      chartData.value.datasets[0].data.push(balance); // Ajoutez le solde initial

      // Maintenant, mettez à jour le solde en fonction des transactions
      sortedTransactions.forEach(transaction => {
        let transactionDate = new Date(transaction.date).toLocaleDateString('fr-FR');
        chartData.value.labels.push(transactionDate);
        if (transaction.category === 'dépense') {
          balance -= transaction.amount;
        } else {
          balance += transaction.amount;
        }
        chartData.value.datasets[0].data.push(balance);
      });
    }
    return {
      chartData,
      chartOptions
    };
  }
};
