import "core-js/modules/es.array.push.js";
import { ref, watchEffect } from 'vue';
import { LineChart } from 'vue-chart-3';
import { Chart, registerables } from 'chart.js';
import axios from 'axios';
Chart.register(...registerables);
export default {
  components: {
    LineChart
  },
  props: {
    accountId: {
      type: Number,
      required: true
    }
  },
  setup(props) {
    const chartData = ref({
      labels: [],
      datasets: [{
        label: 'Total Dépenses',
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
        borderColor: 'rgb(255, 99, 132)',
        fill: false,
        data: []
      }, {
        label: 'Solde',
        backgroundColor: 'rgba(54, 162, 235, 0.5)',
        borderColor: 'rgb(54, 162, 235)',
        fill: false,
        data: []
      }, {
        label: 'Total Revenus',
        backgroundColor: 'rgba(75, 192, 192, 0.5)',
        borderColor: 'rgb(75, 192, 192)',
        fill: false,
        data: []
      }]
    });
    const chartOptions = ref({
      responsive: true,
      maintainAspectRatio: false,
      scales: {
        y: {
          beginAtZero: false
        }
      }
    });
    async function fetchAccountDetailsAndTransactions() {
      try {
        const accountId = props.accountId;
        const accountResponse = await axios.get(`http://localhost:3000/accounts/${accountId}`);
        const accountDetails = accountResponse.data;
        const transactionsResponse = await axios.get(`http://localhost:3000/accounts/${accountId}/transactions`);
        const transactions = transactionsResponse.data;
        updateChartData(accountDetails, transactions);
      } catch (error) {
        console.error('Erreur lors de la récupération des données', error);
      }
    }
    watchEffect(() => {
      if (props.accountId) {
        fetchAccountDetailsAndTransactions();
      }
    });
    function updateChartData(accountDetails, transactions) {
      // Initialize the chart data with the initial balance
      chartData.value.labels = ['Solde Initial'];
      chartData.value.datasets[1].data = [accountDetails.initialBalance]; // This is the balance dataset

      let currentBalance = accountDetails.initialBalance;

      // Now go through transactions and update the balance
      transactions.forEach(transaction => {
        const transactionDate = new Date(transaction.date).toLocaleDateString('fr-FR');
        const amount = transaction.amount;
        if (transaction.type.toLowerCase() === 'dépense') {
          currentBalance -= amount;
        } else if (transaction.type.toLowerCase() === 'revenu') {
          currentBalance += amount;
        }

        // Add the transaction date to the labels if it's not already there
        if (!chartData.value.labels.includes(transactionDate)) {
          chartData.value.labels.push(transactionDate);
        }

        // Update the balance dataset
        chartData.value.datasets[1].data.push(currentBalance);
      });
    }
    return {
      chartData,
      chartOptions
    };
  }
};
