// plugins/vuetify.js
import { createVuetify } from 'vuetify';
import 'vuetify/styles'; // Importation des styles Vuetify
import '@mdi/font/css/materialdesignicons.css'; // Assurez-vous d'importer le CSS ici

const vuetify = createVuetify({
  icons: {
    defaultSet: 'mdi', // Définit le jeu d'icônes par défaut
    aliases: {
      // Ici, vous pouvez configurer des alias pour les icônes
    },
    sets: {
      mdi: {
        component: 'mdi', // Spécifie le composant d'icône à utiliser
      }
    }
  }
  // Autres paramètres de configuration
});

export default vuetify;
